import csv
import random
terms = a["Term"]
freq = a["Freq"]

def gen_random_hex_color():
    hex_digits = '0123456789ABCDEF'

    return '#' + ''.join(
        random.choices(hex_digits, k=6)
    )

f = open('tags.csv', 'w')
fieldnames = ['weight', 'word', 'color', 'url']
writer = csv.DictWriter(f, delimiter=";", fieldnames=fieldnames)
writer.writeheader()
for i in range(len(terms)):
    writer.writerow({'weight': str(int(freq[i])), 'word': str(terms[i]), 'color': str(gen_random_hex_color()), 'url': " "})
